}

/* nav bar */

nav {
  width: 100%;
  height: 70px;
  display: flex;
  background-color: white;
  justify-content: space-between;
  z-index: +45 !important;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  position: sticky;
  top: 0;
}
.logo {
  margin-left: 4%;
  margin-bottom: 0;
  text-decoration: none;
  color: #070505;
  text-transform: uppercase;
}
.logo > img {
  height: 90%;
  width: 70%;
}
nav .nav-bar {
  padding: 20px;
  display: flex;
  width: auto;
  height: 100%;
}
nav .nav-bar .nav-bar_item {
  list-style-type: none;
  padding: 0px 1.5rem;
}
nav .nav-bar .dropdown {
  position: relative;
}
nav .nav-bar .dropdown:after {
  content: "";
  position: absolute;
  right: 0;
  top: 5px;
  width: 7px;
  height: 7px;
  cursor: pointer;
  transform: rotate(-45deg);
  border-left: 2px solid #020101;
  border-bottom: 2px solid #0c0505;
  transition: 0.2s ease-in-out;
}
nav .nav-bar .dropdown:hover:after {
  transform: rotate(135deg);
}
nav .nav-bar .dropdown .project {
  position: absolute;
  transform: translateX(-20px) translateY(5px);
  width: 10rem;
  background: #ffffff;
  padding: 15px 20px;
  border: 1px solid #020101;
  border-radius: 10px;
  height: auto;
  overflow: hidden;
  visibility: hidden;
  transition: 0.2s ease-in-out;
}
nav .nav-bar .dropdown:hover .project {
  height: 200px;
  width: auto;
  visibility: visible;
}

nav .nav-bar .dropdown .project .drop-item {
  list-style-type: none;
  margin-bottom: 10px;
}
nav .nav-bar .nav-bar_item a {
  position: relative;
  display: block;
  padding-bottom: 5px;
  font-size: 20px;
  font-weight: 600;
  text-decoration: none;
  color: #777777;
  text-transform: uppercase;
}
.nav-bar_item > a:hover {
  color: #c6324e !important;
}
.drop-item > a:hover {
  color: #c6324e !important;
}
nav .nav-bar .nav-bar_item a:after {
  content: "";
  position: absolute;
  width: 0%;
  height: 3px;
  bottom: 0;
  left: 0;
  background-color: #e43152;
  transition: 0.2s ease-in-out;
}

nav .nav-bar .nav-bar_item a:hover:after {
  width: 100%;
  z-index: 100 !important;
}

.container {
