  gap: 15px;
}

.calendar h2,
.timeslots h2 {
  text-align: center;
  margin-bottom: 10px;
}

.date {
  width: 100%;
  margin-top: 25px;
  padding: 15px;
  font-size: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  background-color: #fff;
}

.time-option {
  display: block;
  position: relative;
  padding-left: 45px;
  margin-bottom: 15px;
  cursor: pointer;
  font-size: 25px;
  user-select: none;
}

.time-option input {
  position: absolute;
  opacity: 0;
  cursor: pointer;
  height: 0;
  width: 0;
}

.checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
  border-radius: 50%;
}

.time-option:hover input ~ .checkmark {
  background-color: #ccc;
}

.time-option input:checked ~ .checkmark {
  background-color: #ff6347;
}

.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}

.time-option input:checked ~ .checkmark:after {
  display: block;
}

.time-option .checkmark:after {
  top: 9px;
  left: 9px;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: white;
}

.submit {
  cursor: pointer;
  height: 8vh;
  width: 20vh;
  background-color: #52a1d3;
  border: none;
  font-size: x-large;
  margin-left: 40%;
  margin-bottom: 20px;
  font-weight: 600;
  border-radius: 10px;
}

