@import url("https://fonts.googleapis.com/css2?family=Barlow:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap");
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  font-family: "Barlow", sans-serif;
}
body {
  /* height: 100vh; */
  width: 100%;
  background: white;
}

section {
  height: 90vh;
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
  /* gap: 5%; */
}

/* navbar  */

nav {
  width: 100%;
  height: 70px;
  display: flex;
  background-color: white;
  justify-content: space-between;
  z-index: +45 !important;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  position: sticky;
  top: 0;
}
.logo {
  margin-left: 4%;
  margin-bottom: 0;
  text-decoration: none;
  color: #070505;
  text-transform: uppercase;
}
.logo > img {
  height: 90%;
  width: 70%;
}
nav .nav-bar {
  padding: 20px;
  display: flex;
  width: auto;
  height: 100%;
}
nav .nav-bar .nav-bar_item {
  list-style-type: none;
  padding: 0px 1.5rem;
}
nav .nav-bar .dropdown {
  position: relative;
}
nav .nav-bar .dropdown:after {
  content: "";
  position: absolute;
  right: 0;
  top: 5px;
  width: 7px;
  height: 7px;
  cursor: pointer;
  transform: rotate(-45deg);
  border-left: 2px solid #020101;
  border-bottom: 2px solid #0c0505;
  transition: 0.2s ease-in-out;
}
nav .nav-bar .dropdown:hover:after {
  transform: rotate(135deg);
}
nav .nav-bar .dropdown .project {
  position: absolute;
  transform: translateX(-20px) translateY(5px);
  width: 10rem;
  background: #ffffff;
  padding: 15px 20px;
  border: 1px solid #020101;
  border-radius: 10px;
  height: auto;
  overflow: hidden;
  visibility: hidden;
  transition: 0.2s ease-in-out;
}
nav .nav-bar .dropdown:hover .project {
  height: 200px;
  width: auto;
  visibility: visible;
}

nav .nav-bar .dropdown .project .drop-item {
  list-style-type: none;
  margin-bottom: 10px;
}
nav .nav-bar .nav-bar_item a {
  position: relative;
  display: block;
  padding-bottom: 5px;
  font-size: 20px;
  font-weight: 600;
  text-decoration: none;
  color: #777777;
  text-transform: uppercase;
}
.nav-bar_item > a:hover {
  color: #c6324e !important;
}
.drop-item > a:hover {
  color: #c6324e !important;
}
nav .nav-bar .nav-bar_item a:after {
  content: "";
  position: absolute;
  width: 0%;
  height: 3px;
  bottom: 0;
  left: 0;
  background-color: #e43152;
  transition: 0.2s ease-in-out;
}

nav .nav-bar .nav-bar_item a:hover:after {
  width: 100%;
  z-index: 100 !important;
}
/* navbar  */

#idimage {
  width: 140px;
  height: 140px;
  border-radius: 100%;
}
.container {
  margin-top: 2rem;
  width: 88%;
  background: #fff;
  border-radius: 6px;
  padding: 20px 150px 30px 50px;
  box-shadow: 0 5px 10px rgba(0, 0, 0, 0.2);
}
.container .content {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.container .content .left-side {
  width: 35%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  /* margin-top: px; */
  position: relative;
}
.content .left-side::before {
  content: "";
  position: absolute;
  height: 75%;
  width: 3px;
  right: -25%;
  top: 50%;
  transform: translateY(-50%);
  background: #afafb6;
  -webkit-transform: translateY(-50%);
  -moz-transform: translateY(-50%);
  -ms-transform: translateY(-50%);
  -o-transform: translateY(-50%);
}
.content .left-side .details {
  margin: 20px;
  text-align: center;
}

.content .left-side .details p {
  padding: 2px;
}
.content .left-side .details i {
  font-size: 40px;
  color: #26ccca;
  margin-bottom: 10px;
}

.content .topic {
  font-size: 18px;
  font-weight: 500;
  text-align: left;
}

.content-table {
  border-collapse: collapse;
  margin: 35px;
  font-size: 1.2em;
  min-width: 650px;
  min-height: 450px;
  border-radius: 5px 5px 0 0;
  overflow: hidden;
  margin-left: 25%;
  /*margin-right: 450px;
  */
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
  -webkit-border-radius: 5px 5px 0 0;
  -moz-border-radius: 5px 5px 0 0;
  -ms-border-radius: 5px 5px 0 0;
  -o-border-radius: 5px 5px 0 0;
}

.content-table thead tr {
  background-color: #26ccca;
  color: #ffffff;
  text-align: left;
  font-weight: bold;
}

.content-table th,
.content-table td {
  padding: 30px 20px;
  padding-left: 50px;
}

.content-table tbody tr {
  border-bottom: 1px solid #dddddd;
}

.content-table tbody tr:last-of-type {
  border-bottom: 2px solid #53b5a1;
}

.footer {
  height: auto;
  border-radius: 6px;
  margin-top: 5rem;
  box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
}

