/* general styles */

:root {
  --clr-neutral-100: hsl(0, 0%, 100%);
  --clr-primary-100: hsl(205, 15%, 58%);
  --clr-primary-400: hsl(215, 25%, 27%);
  --clr-primary-800: hsl(217, 33%, 17%);
  --clr-primary-900: hsl(218, 33%, 9%);
}

/*Parent div*/
body {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
}

.parentdiv {
  margin-top: 7.5vh;
  margin-bottom: 10vh;
  width: 75vw;
  height: 62vw;
  background-color: #c8def5;
  border-radius: 50px;
}

.head {
  font-weight: 350;
  margin-top: 7.5vh;
  font-size: 40px;
  letter-spacing: 1.5px;
}

.rowdiv {
  background-color: whitesmoke;
  height: 18vw;
  width: 20vw;
  margin: 2.5vw;
  border-radius: 30px;
}
.row1 {
  display: flex;
  margin-top: 1.3vw;
}

.number-input {
  background-color: aliceblue;
  height: 4vw;
  width: 20vw;
  border-radius: 15px;
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  margin-left: 2.5vw;
}
.number-input button {
  border: none;
  width: 4vw;
  height: 3vw;
}

.number-input button,
.number-input input {
  font-size: 1.5vw;
  background-color: #ffffff;
  border-radius: 10px;
  text-align: center;
  border: 1px solid rgb(231, 231, 129);
}
.number-input input {
  width: 10vw;
  height: 3vw;
}

.itemimg {
  width: 80%;
  margin: 1.5vw;
  transition: transform 0.3s ease;
}
.itemimg:hover {
  transform: scale(1.1);
  -webkit-transform: scale(1.1);
  -moz-transform: scale(1.1);
  -ms-transform: scale(1.1);
  -o-transform: scale(1.1);
}

.cloth {
  margin-left: 2vw;
}

.butt {
  font-size: 35px;
  width: 100%;
  height: 80%;
  border: none;
  background: none;
  cursor: pointer;
  line-height: 1;
  color: rgba(165, 42, 42, 0.884);
}
.rowdiv {
  position: relative; /* Position relative for absolute positioning */
}

.hover-overlay {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
  height: 0; /* Initially set height to 0 */
  background-color: rgba(0, 0, 0, 0.5); /* Semi-transparent background */
  justify-content: center;
  align-items: center;
  overflow: hidden; /* Hide overflow */
  transition: height 0.3s ease; /* Add transition effect to height property */

  border-bottom-left-radius: 30px;
  border-bottom-right-radius: 30px;
}

.item1:hover .hover-overlay {
  height: 25%;
}

.hover-text {
  color: white; /* Text color */
  font-size: 18px; /* Text size */
  text-align: center; /* Center text */
  margin-top: 12px;
}

.nextbut {
  margin-left: 48%;
  font-size: 25px;
  /* margin-bottom: 12vw; */
  padding: 0.7vw;
  border-radius: 10px;
  padding-left: 1vw;
  padding-right: 1vw;
  background-color: #ec778c;
  border: none;
  color: rgb(75, 19, 19);
  font-weight: 480;
}

.footer {
  height: auto;
  width: 100%;
  border-radius: 6px;
  margin-top: 5rem;
  background-color: #c8def5;
}

