}
body {
  background-color: #e0eefe !important ;
}

/* nav bar */

nav {
  width: 100%;
  height: 70px;
  display: flex;
  background-color: white;
  justify-content: space-between;
  z-index: +45 !important;
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.1);
  position: sticky; /* Add this */
  top: 0;
}
.logo {
  margin-left: 4%;
  margin-bottom: 0;
  text-decoration: none;
  color: #070505;
  text-transform: uppercase;
}
.logo > img {
  height: 90%;
  width: 70%;
}
nav .nav-bar {
  padding: 20px;
  display: flex;
}
nav .nav-bar .nav-bar_item {
  list-style-type: none;
  padding: 0px 20px;
}

nav .nav-bar .nav-bar_item a {
  position: relative;
  display: block;
  padding-bottom: 5px;
  font-size: 20px;
  font-weight: 600;
  text-decoration: none;
  color: #777777;
  text-transform: uppercase;
}
.nav-bar_item > a:hover {
  color: #52a1d3 !important;
}
.drop-item > a:hover {
  color: #52a1d3 !important;
}
nav .nav-bar .nav-bar_item a:after {
  content: "";
  position: absolute;
  width: 0%;
  height: 3px;
  bottom: 0;
  left: 0;
  background-color: #52a1d3;
  transition: 0.2s ease-in-out;
}

nav .nav-bar .nav-bar_item a:hover:after {
  width: 100%;
  z-index: 100 !important;
}

.fa-brands,
.fas {
  font-size: 30px;
}

.topshape,
.maindiv,
.line,
.addhead,
.buttons {
  display: flex;
  justify-content: center;
  align-items: center;
}

button {
  margin: 40px;
  padding: 10px;
  width: 120px;
  border-radius: 10px;
  background-color: #52a1d3;
  font-size: 20px;
  border: none;
  margin-top: 40px;
}

.topimg {
  width: 15vw;
  padding-bottom: 40px;
  padding-top: 40px;
}
.parentdiv {
  width: 75vw;
  height: 85vh;
  background-color: #ffffff;
  border-radius: 40px;
  padding: 10vw;
  padding-top: 4vw;
  margin-left: 5vw;
  margin-bottom: 15px;
}

.head {
  font-weight: 450;
  font-size: 25px;
  letter-spacing: 1.5px;
  text-align: center;
  padding-bottom: 20px;
}

.parentdiv > h2 {
  text-align: center;
  font-size: xx-large;
}

.wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  background-color: #f4f4f9;
}

.date-time-picker {
  margin-top: 8%;
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding: 20px;
}

.calendar,
.timeslots {
  flex-basis: 45%;
}

.calendar {
  padding: 10px;
}
.timeslots {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  gap: 15px;
}

.calendar h2,
.timeslots h2 {
  text-align: center;
  margin-bottom: 10px;
}

.date {
  width: 100%;
  margin-top: 25px;
  padding: 15px;
  font-size: 20px;
  border: 1px solid #ccc;
  border-radius: 5px;
  background-color: #fff;
}

.time-option {
  display: block;
  position: relative;
  padding-left: 45px;
  margin-bottom: 15px;
  cursor: pointer;
  font-size: 25px;
  user-select: none;
}

.time-option input {
  position: absolute;
  opacity: 0;
  cursor: pointer;
  height: 0;
  width: 0;
}

.checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
  border-radius: 50%;
}

.time-option:hover input ~ .checkmark {
  background-color: #ccc;
}

.time-option input:checked ~ .checkmark {
  background-color: #ff6347;
}

.checkmark:after {
  content: "";
  position: absolute;
  display: none;
}

.time-option input:checked ~ .checkmark:after {
  display: block;
}

.time-option .checkmark:after {
  top: 9px;
  left: 9px;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: white;
}

.submit {
  cursor: pointer;
  height: 8vh;
  width: 20vh;
  background-color: #52a1d3;
  border: none;
  font-size: x-large;
  margin-left: 40%;
  margin-bottom: 20px;
  font-weight: 600;
  border-radius: 10px;
}

