  margin-top: 2rem;
  min-height: 15rem;
}
.box > .img {
  height: 5.2rem;
  width: 5.2rem;
  border-radius: 50%;
  border: 1px solid black;
  background-color: rgb(252, 245, 255);
  position: absolute;
  left: 35%;
  top: -20%;
}

.box > h2,
p {
  text-align: center;
  margin: 5px;
}
