/* CareConnect Dashboard Styles */

.dashboard-container {
    display: flex;
    min-height: 100vh;
    background-color: #f8f9fa;
}

/* Enhanced Collapsible Sidebar Styles */
.sidebar {
    width: 280px;
    background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
    color: white;
    box-shadow: 2px 0 15px rgba(0,0,0,0.15);
    position: fixed;
    height: 100vh;
    overflow-y: auto;
    z-index: 1000;
    transition: all 0.3s cubic-bezier(0.4, 0.0, 0.2, 1);
    transform: translateX(0);
}

.sidebar.collapsed {
    width: 80px;
}

.sidebar.collapsed .nav-text,
.sidebar.collapsed .brand-text,
.sidebar.collapsed .user-details {
    opacity: 0;
    visibility: hidden;
    width: 0;
    overflow: hidden;
    transition: all 0.2s ease;
}

.sidebar:not(.collapsed) .nav-text,
.sidebar:not(.collapsed) .brand-text,
.sidebar:not(.collapsed) .user-details {
    opacity: 1;
    visibility: visible;
    transition: all 0.3s ease 0.1s;
}

.sidebar-header {
    padding: 1.5rem;
    border-bottom: 1px solid rgba(255,255,255,0.2);
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: relative;
    min-height: 80px;
}

.sidebar-brand {
    display: flex;
    align-items: center;
    flex: 1;
    transition: all 0.3s ease;
}

.brand-logo {
    font-size: 1.8rem;
    margin-right: 0.75rem;
    display: flex;
    align-items: center;
    justify-content: center;
    width: 40px;
    height: 40px;
    border-radius: 10px;
    background: rgba(255,255,255,0.1);
    transition: all 0.3s ease;
}

.sidebar.collapsed .brand-logo {
    margin-right: 0;
}

.brand-text {
    font-weight: 600;
    font-size: 1.3rem;
    margin: 0;
    white-space: nowrap;
    transition: all 0.3s ease;
}

.sidebar-toggle {
    background: rgba(255,255,255,0.15);
    border: none;
    color: white;
    width: 35px;
    height: 35px;
    border-radius: 8px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.9rem;
}

.sidebar-toggle:hover {
    background: rgba(255,255,255,0.25);
    transform: scale(1.05);
}

.notification-bell {
    position: relative;
    background: rgba(255,255,255,0.15);
    border-radius: 8px;
    width: 35px;
    height: 35px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    transition: all 0.3s ease;
    margin-left: 0.5rem;
}

.notification-bell:hover {
    background: rgba(255,255,255,0.25);
    transform: scale(1.05);
}

.notification-badge {
    position: absolute;
    top: -8px;
    right: -8px;
    font-size: 10px;
    min-width: 18px;
    height: 18px;
    border-radius: 9px;
    display: flex;
    align-items: center;
    justify-content: center;
    animation: pulse 2s infinite;
}


.sidebar-nav {
    padding: 1rem 0;
}

.nav-list {
    list-style: none;
    padding: 0;
    margin: 0;
}

.nav-item {
    margin: 0.25rem 0;
}

.nav-link {
    display: flex;
    align-items: center;
    padding: 0.85rem 1.5rem;
    color: rgba(255,255,255,0.9);
    text-decoration: none;
    transition: all 0.3s cubic-bezier(0.4, 0.0, 0.2, 1);
    border-left: 3px solid transparent;
    border-radius: 0 25px 25px 0;
    margin: 0.25rem 0;
    position: relative;
    overflow: hidden;
}

.nav-link::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.1), transparent);
    transition: left 0.5s;
}

.nav-link:hover::before {
    left: 100%;
}

.nav-link:hover {
    background-color: rgba(255,255,255,0.15);
    color: white;
    border-left-color: #28a745;
    transform: translateX(5px);
    padding-left: 2rem;
}

.sidebar.collapsed .nav-link:hover {
    transform: translateX(0);
    padding-left: 1.5rem;
    border-radius: 12px;
    margin: 0.25rem 0.5rem;
}

.nav-link.active {
    background-color: rgba(255,255,255,0.25);
    color: white;
    border-left-color: #ffc107;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    font-weight: 600;
}

.nav-link.active::after {
    content: '';
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 4px;
    height: 60%;
    background: #ffc107;
    border-radius: 2px 0 0 2px;
}

.nav-link i {
    width: 24px;
    height: 24px;
    margin-right: 0.75rem;
    text-align: center;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.1rem;
    transition: all 0.3s ease;
    flex-shrink: 0;
}

.nav-link:hover i {
    transform: scale(1.1) rotate(5deg);
}

.nav-text {
    white-space: nowrap;
    transition: all 0.3s ease;
    font-weight: 500;
}

.sidebar.collapsed .nav-link {
    padding: 0.85rem;
    margin: 0.25rem 0.5rem;
    border-radius: 12px;
    justify-content: center;
    border-left: none;
}

.sidebar.collapsed .nav-link i {
    margin-right: 0;
    font-size: 1.2rem;
}

.nav-divider {
    height: 1px;
    background-color: rgba(255,255,255,0.2);
    margin: 1rem 0;
}

.sidebar-footer {
    position: absolute;
    bottom: 0;
    width: 100%;
    padding: 1rem;
    border-top: 1px solid rgba(255,255,255,0.2);
}

.user-info {
    display: flex;
    align-items: center;
    transition: all 0.3s ease;
}

.sidebar.collapsed .user-info {
    justify-content: center;
}

.sidebar.collapsed .user-avatar {
    margin-right: 0;
}

.user-avatar {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 0.75rem;
