}

.stat-card.bg-primary .stat-icon {
    background-color: #007bff;
}

.stat-card.bg-success .stat-icon {
    background-color: #28a745;
}

.stat-card.bg-info .stat-icon {
    background-color: #17a2b8;
}

.stat-card.bg-warning .stat-icon {
