}

/* Badges */
.badge {
    font-size: 0.75rem;
    font-weight: 500;
    margin: 0.125rem;
}

/* Buttons */
.btn {
    border-radius: 6px;
    font-weight: 500;
    transition: all 0.3s ease;
}

.btn:hover {
    transform: translateY(-1px);
}

/* Enhanced Responsive Design */
@media (max-width: 992px) {
    .sidebar {
        width: 280px;
        transform: translateX(-100%);
    }

    .sidebar.mobile-open {
        transform: translateX(0);
    }

    .main-content {
        margin-left: 0;
        padding: 1.5rem;
    }

    .sidebar.collapsed + .main-content,
    .sidebar.collapsed ~ .main-content,
    body:has(.sidebar.collapsed) .main-content {
        margin-left: 0;
    }
}

@media (max-width: 768px) {
    .sidebar {
        width: 100%;
        height: 100vh;
        transform: translateX(-100%);
        z-index: 1050;
    }

    .sidebar.mobile-open {
        transform: translateX(0);
    }

    .main-content {
        margin-left: 0;
        padding: 1rem;
    }

    .dashboard-container {
