}

.mobile-sidebar-toggle:hover {
    transform: scale(1.05);
    box-shadow: 0 6px 20px rgba(0,0,0,0.3);
}

@media (max-width: 992px) {
    .mobile-sidebar-toggle {
        display: flex;
        align-items: center;
        justify-content: center;
    }

    .sidebar-header .sidebar-toggle {
        display: none;
    }
}

/* Smooth Animation Classes */
.sidebar-animating {
    pointer-events: none;
}

/* Responsive CSS Grid Layout System */
.dashboard-grid {
    display: grid;
    gap: 1.5rem;
    width: 100%;
}

/* Stats Grid - Horizontal layout for desktop */
.stats-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 1.5rem;
    margin-bottom: 2rem;
}

/* For laptop/desktop: prefer 4 columns when space allows */
@media (min-width: 1200px) {
    .stats-grid {
        grid-template-columns: repeat(4, 1fr);
        gap: 2rem;
    }
}

/* For tablets: 2 columns */
@media (min-width: 768px) and (max-width: 1199px) {
    .stats-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 1.5rem;
    }
}

/* Actions Grid - Horizontal flow */
.actions-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 1.5rem;
    margin-bottom: 1.5rem;
}

@media (min-width: 1200px) {
    .actions-grid {
        grid-template-columns: repeat(3, 1fr);
        gap: 2rem;
    }
}

/* Content Grid - Two column layout for desktop */
.content-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
}

@media (min-width: 1200px) {
    .content-grid {
        grid-template-columns: 2fr 1fr;
        gap: 2.5rem;
    }

    .content-grid.three-column {
        grid-template-columns: 1fr 1fr 1fr;
    }

    .content-grid.analytics {
        grid-template-columns: 1fr 1fr;
    }
}

/* Volunteer/NGO Cards Grid */
.cards-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(320px, 1fr));
    gap: 1.5rem;
}

@media (min-width: 1200px) {
    .cards-grid {
        grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
        gap: 2rem;
    }
}

/* Donation Requests Grid */
.requests-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 1.5rem;
}

@media (min-width: 1200px) {
    .requests-grid {
        grid-template-columns: repeat(3, 1fr);
        gap: 2rem;
    }
}

/* Charts and Analytics Grid */
.charts-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
}

@media (min-width: 992px) {
    .charts-grid {
        grid-template-columns: 1fr 1fr;
        gap: 2rem;
    }
}

@media (min-width: 1400px) {
    .charts-grid.four-column {
        grid-template-columns: repeat(4, 1fr);
        gap: 2rem;
    }
}

/* Full-width elements */
.full-width {
    grid-column: 1 / -1;
}

/* Map containers - responsive sizing */
.map-container {
    border-radius: 12px;
    overflow: hidden;
    transition: all 0.3s ease;
    width: 100%;
    height: 400px;
}

@media (min-width: 1200px) {
    .map-container {
        height: 70vh;
        min-height: 500px;
        max-height: 800px;
    }
}

.map-container:hover {
    transform: scale(1.01);
    box-shadow: 0 8px 25px rgba(0,0,0,0.2);
}

/* Enhanced Grid Item Animations */
.grid-item {
    transition: all 0.3s cubic-bezier(0.4, 0.0, 0.2, 1);
}

.grid-item:hover {
    transform: translateY(-2px);
    z-index: 2;
}

/* Masonry-like layout for mixed content */
.masonry-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(350px, 1fr));
    gap: 1.5rem;
    align-items: start;
}

@media (min-width: 1200px) {
    .masonry-grid {
        grid-template-columns: repeat(3, 1fr);
        gap: 2rem;
    }
}

/* Compact grid for smaller cards */
.compact-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 1rem;
}

@media (min-width: 1200px) {
    .compact-grid {
        grid-template-columns: repeat(6, 1fr);
        gap: 1.5rem;
    }
}

/* Grid utilities */
.grid-span-2 {
    grid-column: span 2;
}

.grid-span-3 {
    grid-column: span 3;
}

.grid-span-full {
    grid-column: 1 / -1;
}

/* Responsive adjustments for collapsed sidebar */
.sidebar.collapsed ~ .main-content .stats-grid,
body:has(.sidebar.collapsed) .main-content .stats-grid {
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
}

@media (min-width: 1200px) {
    .sidebar.collapsed ~ .main-content .stats-grid,
    body:has(.sidebar.collapsed) .main-content .stats-grid {
        grid-template-columns: repeat(5, 1fr);
    }
}

/* Additional Dashboard Elements */

/* Donation Request Cards */
.donation-request-card {
    background: white;
    border-radius: 12px;
    border: 1px solid #e9ecef;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    overflow: hidden;
}

.donation-request-card .card-header {
    background: linear-gradient(135deg, #f8f9fa, #e9ecef);
