}

/* Impact Statistics */
.impact-stat {
    padding: 1.5rem;
    text-align: center;
}

.impact-number {
    font-size: 3rem;
    font-weight: 700;
    line-height: 1;
    margin-bottom: 0.5rem;
}

.impact-label {
    font-size: 1rem;
    color: #6c757d;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

/* Tracking Timeline */
.tracking-timeline {
    position: relative;
    padding: 1rem 0;
}

.timeline-item {
    display: flex;
    align-items: flex-start;
    margin-bottom: 2rem;
    position: relative;
}

.timeline-item:last-child {
    margin-bottom: 0;
}

.timeline-item:not(:last-child)::after {
    content: '';
    position: absolute;
    left: 20px;
    top: 45px;
    width: 2px;
    height: calc(100% + 2rem);
    background-color: #e9ecef;
    z-index: 1;
}

.timeline-item.completed:not(:last-child)::after {
    background-color: #28a745;
}

.timeline-icon {
    width: 40px;
    height: 40px;
    border-radius: 50%;
    background-color: #e9ecef;
    color: #6c757d;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-right: 1rem;
    flex-shrink: 0;
    position: relative;
    z-index: 2;
}

.timeline-item.completed .timeline-icon {
    background-color: #28a745;
    color: white;
}

.timeline-item.active .timeline-icon {
    background-color: #007bff;
    color: white;
    box-shadow: 0 0 0 4px rgba(0,123,255,0.2);
}

.timeline-content {
    flex-grow: 1;
    padding-top: 0.25rem;
}

.timeline-content h6 {
    margin: 0 0 0.25rem 0;
    color: #2c3e50;
    font-weight: 600;
}

.timeline-content p {
    margin: 0;
    font-size: 0.9rem;
}

/* Enhanced Responsive Design */
@media (max-width: 992px) {
    .action-card {
        flex-direction: column;
        text-align: center;
    }

    .action-icon {
        margin-right: 0;
        margin-bottom: 1rem;
