    .impact-number {
        font-size: 2.5rem;
    }
}

@media (max-width: 576px) {
    .donation-request-card .card-header {
        flex-direction: column;
        align-items: flex-start;
        gap: 0.5rem;
    }

    .card-actions {
        flex-direction: column;
